def get_excle(area,detail_url):

    for url in detail_url:

        print(url)
        headers = {
            'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/85.0.4183.83 Safari/537.36',
        }
        import time
        time.sleep(2)
        response = requests.request(method='get', url=url, headers=headers).text
        from lxml import etree
        tree = etree.HTML(response)
        title = ''.join(tree.xpath('//div[@id="title"]/text()')).strip().replace('(', '（').replace(')', '）')
        down_load = re.findall(r'<a href="(.*?)"', response, re.S)

        for down_load_url in down_load:
            down_url = 'http://zhejiang.chinatax.gov.cn' + down_load_url
            if down_load_url.endswith('.xls'):
                title = title + '.xls'
            elif down_load_url.endswith('.xlsx'):
                title = title + '.xlsx'
            elif down_load_url.endswith('.doc'):
                title = title + '.doc'
            elif down_load_url.endswith('.docx'):
                title = title + '.docx'
            else:
                continue

            print(title, '正在下载')
            time.sleep(1)
            content = requests.get(url=down_url, headers=headers).content
            import os
            if not os.path.exists(r'D:\projects\Spider\local_spider\Other\spider_all\Certificate\税务非正常户\{}'.format(area)):
                os.mkdir(r'D:\projects\Spider\local_spider\Other\spider_all\Certificate\税务非正常户\{}'.format(area))
            with open(r'{}/{}'.format(area,title), 'wb')as fp:
                fp.write(content)

            if title.endswith('.doc'):
                title = title.replace('.doc','')
                import win32com.client as wc
                word = wc.Dispatch("Word.Application")
                doc = word.Documents.Open(r"D:\projects\Spider\local_spider\Other\spider_all\Certificate\税务非正常户\{}\{}.doc".format(area,title))
                # 上面的地方只能使用完整绝对地址，相对地址找不到文件，且，只能用“\\”，不能用“/”，哪怕加了 r 也不行，涉及到将反斜杠看成转义字符。
                doc.SaveAs(r"D:\projects\Spider\local_spider\Other\spider_all\Certificate\税务非正常户\{}\{}.docx".format(area,title), 12)  # 转换后的文件,12代表转换后为docx文件
                # doc.SaveAs(r"F:\\***\\***\\appendDoc\\***.docx", 12)#或直接简写
                # 注意SaveAs会打开保存后的文件，有时可能看不到，但后台一定是打开的
                doc.Close()
                import os
                os.remove(r"D:\projects\Spider\local_spider\Other\spider_all\Certificate\税务非正常户\{}\{}.doc".format(area,title))
            break


